create table address (address_id bigint not null auto_increment, city varchar(255), state varchar(255), street_address varchar(255), zip varchar(255), primary key (address_id))
create table admin_user (admin_user_id bigint not null auto_increment, user_id bigint, primary key (admin_user_id))
create table customer (customer_id bigint not null auto_increment, event_id bigint, ticket_id bigint, user_id bigint, primary key (customer_id))
create table events (event_id bigint not null auto_increment, created_by_user_id bigint, created_on datetime, event_date_from datetime, event_date_to datetime, event_description varchar(255), event_location varchar(255), event_name varchar(255), event_time_from varchar(255), event_time_to varchar(255), event_type varchar(255), file_path varchar(255), organized_by varchar(255), price double precision, total_seating_capacity varchar(255), total_ticket_remaining integer, zip_code varchar(255), primary key (event_id))
create table facility (facility_id bigint not null auto_increment, added_by_user_info_id bigint, booked_from datetime, booked_to datetime, facility_description varchar(255), facility_location varchar(255), facility_name varchar(255), is_active_for_rental bit, price_per_hour double precision, school_id bigint, total_seating_capacity varchar(255), updated_by_user_info_id bigint, primary key (facility_id))
create table school (school_id bigint not null auto_increment, phone varchar(255), school_address_id bigint, school_name varchar(255), school_rep_id bigint, primary key (school_id))
create table school_representative (school_rep_id bigint not null auto_increment, school_id bigint, user_id bigint, primary key (school_rep_id))
create table user_info (user_info_id bigint not null auto_increment, account_type varchar(255), active integer, address_id bigint, created_date datetime, date_of_birth date, email varchar(255), first_name varchar(255), last_name varchar(255), password varchar(255), phone_number varchar(255), primary key (user_info_id))
create table user_info_user_role (user_info_id bigint not null, user_role_id bigint not null, primary key (user_info_id, user_role_id))
create table user_role (user_role_id bigint not null auto_increment, role_short_description varchar(255), user_role varchar(255), primary key (user_role_id))
